#!/usr/bin/env ruby

require 'rubygems'
require 'bundler/setup'
Bundler.require

identity_token = ''

customer = VaultedBilling::Customer.new
credit_card = VaultedBilling::CreditCard.new({
  card_number: '5454545454545454',
  expires_on: Date.new(2010, 12, 31),
  cvv_number: '123'
})

g = VaultedBilling.gateway(:ipcommerce).new({
  username: identity_token,
  # service_key_store: store,
  application_id: '66817',
  service_id: 'E4FB800001'
})

auth = g.authorize(customer, credit_card, '15.00', {
  merchant_profile_id: 'AutoTest_E4FB800001',
  service_id: 'E4FB800001',
  employee_id: '12345',
  order_id: '12345',
  card_type_id: '1'
})


capture = g.capture(auth.id, '15.00', { service_id: 'E4FB800001' })